package learnautomationtestingweek1day1;

public class LearnDataTypes {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
//Declare the values
		//Age is variable
		//usually we use int but for learning purpose we used byte
		//for variable if i use 1 word i need to use lowercase if i'm using more than 1 word i need to use camel case
		//ageOfPerson
		
		//byte
		byte age=30;
		System.out.println(age);
		
		//short
		short ageOfPerson=128;
		System.out.println(ageOfPerson);

		//int
		int amount=50000;
		System.out.println(amount);

		//long
		long mobileNumber=9940153765L;
		System.out.println(mobileNumber);
		
		//float
		float rupee= 1.123456789f;
		System.out.println(rupee);
		
		//double
		double dollar=1.123456789;
		System.out.println(dollar);
		
		//char
		char logo='V';
		System.out.println(logo);

		//boolean
		boolean haveBreakFast=true;
		System.out.println(haveBreakFast);
		
		//String
		String name="Vinothkumar";
		System.out.println(name);
		
		//Print 2 variables alone
		System.out.println(name+" "+age);
		
		//Print a string and variable like My name is Vinothkumar
		System.out.println("My name is "+name);


		
		

		
	}
	

}
